import React from "react";
import "./Homepage.css";
import Navbar from '../NavBar/Navbar'
import { Link } from "react-router-dom";
import CreatePostModal from "./CreatePostModal";
import PostPreview from "./PostPreview"
import TrendingPreview from "./TrendingPreview";

class Homepage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      openModal: false

    };
    this.loadPosts();
  }

  loadPosts() {
    fetch("http://stark.cse.buffalo.edu/cse410/blackhole/api/postcontroller.php", {
      method: "post",
      body: JSON.stringify({
        action: "getPosts",
        posttype:"post",
        max_posts: "12",
      })
    })
      .then(res => res.json())
      .then(
        result => {
          if (result.posts) {
            this.setState({
              posts: result.posts
            });
          }
        },
        error => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      );
  }






  render() {
    return (
        <html>
      <head className="test">
          <body className="test">
        <Navbar/>
          <div className="homepage-main">
            <div className="homepage-trending-group">
              <TrendingPreview/>
              <TrendingPreview/>
              <TrendingPreview/>
              <TrendingPreview/>
              <TrendingPreview/>


            </div>
            <div className="homepage-button-group">

              <button className="post-button-b" onClick={e => toggleModal(this, e)}>Create Post B</button>
            </div>
            <hr className="hr" />

            <div className="homepage-posts-group">
                {this.state.posts.map(post => (
                    <PostPreview post={post}></PostPreview>
                ))}

            </div>

            <CreatePostModal show={this.state.openModal} onClose={e => toggleModal(this, e)}>
              This is a modal dialog!
                </CreatePostModal>
          </div>
        </body>
      </head>
        </html>


    );
  }
}

function toggleModal(app) {
  app.setState({
    openModal: !app.state.openModal
  });
}


export default Homepage;
