    state = {
        username: "",
        email: "", 
        password: ""
    };

    change = (e) => {
        this.setState({         // change function used to change the state of the Component
            [e.target.name]:    
            e.target.value});    // Study this syntax for JS/JSX
    };

    onSubmit = e =>{
        e.preventDefault();
        this.props.onSubmit(this.state)
        this.setState({
            //username: "",
            email: "", 
            password: ""
            }
        );
    }

